sp_widget.client_script:
function($rootScope,$scope, spUtil) {
	/* widget controller */
	var c = this;

	spUtil.addInfoMessage("Welcome to the Comex Page")

	c.selectItem = function(idx) {
		var id = c.data.request[idx].sys_id;
		console.log('Request ID: ' + id);
		$rootScope.requestID = id;
		$rootScope.$emit('selectRequest', id);
	}
}

sp_widget.script:
(function() {
	//create an array to populate with requests
	data.request = [];
	var requestV = new GlideRecord('x_420177_comex_vacation_rquest');

	requestV.addQuery('sys_created_by', gs.getUserName());
	requestV.orderByDesc('sys_created_on');
	requestV.query();
	console.log(requestV);
	while (requestV.next()) {
		var requestObj = {};

		//use service portal helper method to get some display values
		$sp.getRecordDisplayValues(requestObj, requestV, 'u_user_request, u_integer_days, sys_id');

		//push the populated obj into the array
		data.request.push(requestObj);
	}
})();
